package com.itwill.dto;

public class Book {
	private int bookNo;
	private String id;
	private String name;
	private String phone;
	private int rstNo;
	private int bookNop;
	private String bookDate;
	private String bookTime;
	private String bookCmt;
	private int bookStatus;
	private String rstName;
	private String mainImg;
	
	
	public Book() {
		// TODO Auto-generated constructor stub
	}

	public int getBookNo() {
		return bookNo;
	}

	public void setBookNo(int bookNo) {
		this.bookNo = bookNo;
	}

	public String getId() {
		return id;
	}

	public void setId(String id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getPhone() {
		return phone;
	}

	public void setPhone(String phone) {
		this.phone = phone;
	}

	public int getRstNo() {
		return rstNo;
	}

	public void setRstNo(int rstNo) {
		this.rstNo = rstNo;
	}

	public int getBookNop() {
		return bookNop;
	}

	public void setBookNop(int bookNop) {
		this.bookNop = bookNop;
	}

	public String getBookDate() {
		return bookDate;
	}

	public void setBookDate(String bookDate) {
		this.bookDate = bookDate;
	}

	public String getBookTime() {
		return bookTime;
	}

	public void setBookTime(String bookTime) {
		this.bookTime = bookTime;
	}

	public String getBookCmt() {
		return bookCmt;
	}

	public void setBookCmt(String bookCmt) {
		this.bookCmt = bookCmt;
	}

	public int getBookStatus() {
		return bookStatus;
	}

	public void setBookStatus(int bookStatus) {
		this.bookStatus = bookStatus;
	}

	public String getRstName() {
		return rstName;
	}

	public void setRstName(String rstName) {
		this.rstName = rstName;
	}

	public String getMainImg() {
		return mainImg;
	}

	public void setMainImg(String mainImg) {
		this.mainImg = mainImg;
	}
	
	
	

}
